const pintoBehaviorName = eventsFunctionContext.getBehaviorName("Behavior");
/** @type {Hashtable<gdjs.RuntimeObject[]>} */
const pintoObjectsLists = eventsFunctionContext.getObjectsLists("Object");
/** @type {Hashtable<gdjs.RuntimeObject[]>} */
const baseObjectsLists = eventsFunctionContext.getObjectsLists("BaseObject");

eventsFunctionContext.returnValue = gdjs.evtTools.obj.twoListsTest(
  (pintoObject, baseObject, pintoBehaviorName) => {
    const behavior = pintoObject.getBehavior(pintoBehaviorName);
    return behavior.baseObject === baseObject;
  },
  pintoObjectsLists,
  baseObjectsLists,
  false,
  pintoBehaviorName
);

const objectMask = eventsFunctionContext.getObjects("Mask")[0];
if (!objectMask) return;

const objectsMasked = eventsFunctionContext.getObjects("Masked");
for (const objectMasked of objectsMasked) {
    const renderedMasked = objectMasked.getRendererObject(); 
    renderedMasked.mask = objectMask.getRendererObject();
}

const bhName = eventsFunctionContext.getBehaviorName("Behavior");
const obj = objects[0];
const behavior = obj.getBehavior(bhName);

if (!runtimeScene.__allPinnedTo)
    runtimeScene.__allPinnedTo = new Set();

const prototype = Object.getPrototypeOf(behavior);
if (!prototype.updateRelativeCoords) {
    gdjs.registerObjectDeletedFromSceneCallback(function (runtimeScene, deletedObject) {
        if (!runtimeScene.__allPinnedTo) return;

        for (const pinto of runtimeScene.__allPinnedTo) {
            if (pinto.baseObject === deletedObject) {
                pinto.baseObject = null;
            }
        }
    });

    prototype.updateRelativeCoords = function (baseObject) {
        const obj = this.owner;
        this.relativeX = obj.getX() - this.basisOldX;
        this.relativeY = obj.getY() - this.basisOldY;
    }

    prototype.updateOldCoords = function (baseObject) {
        const obj = this.owner;
        this.ownerOldX = obj.getX();
        this.ownerOldY = obj.getY();
        this.basisOldX = baseObject.getX();
        this.basisOldY = baseObject.getY();
    }
    prototype.followBaseObject = function () {
        if (this.followingDone) {
            return;
        }
        this.followingDone = true;
        /** @type {gdjs.RuntimeObject} */
        const baseObject = this.baseObject;
        if (baseObject) {

            if (baseObject.hasBehavior(bhName)) {
                const basisBehavior = baseObject.getBehavior(bhName);
                if (basisBehavior.type === this.type) {
                    basisBehavior.followBaseObject();
                }
            }

            const obj = this.owner;            
            if (obj.getX() !== this.ownerOldX
                || obj.getY() !== this.ownerOldY) {
                this.updateRelativeCoords(baseObject);
            }

            if (this.basisOldX !== baseObject.getX() ||
                this.basisOldY !== baseObject.getY()) {
                obj.setPosition(
                    baseObject.getX() + this.relativeX,
                    baseObject.getY() + this.relativeY);
            }

            this.updateOldCoords(baseObject);
        }
    }
}
runtimeScene.__allPinnedTo.add(behavior);

const bhName = eventsFunctionContext.getBehaviorName("Behavior");
const obj = objects[0];
const behavior = obj.getBehavior(bhName);
behavior.followingDone = false;

const bhName = eventsFunctionContext.getBehaviorName("Behavior");
const obj = objects[0];
const behavior = obj.getBehavior(bhName);

behavior.followBaseObject();

const obj = objects[0];
const bhName = eventsFunctionContext.getBehaviorName("Behavior");
const baseObjects = eventsFunctionContext.getObjects("BaseObject");
if (baseObjects.length === 0) return;
const baseObject = baseObjects[0];
const behavior = obj.getBehavior(bhName);
behavior.baseObject = baseObject;
behavior.updateOldCoords(baseObject);
behavior.updateRelativeCoords(baseObject);

const obj = objects[0];
const bhName = eventsFunctionContext.getBehaviorName("Behavior");
const behavior = obj.getBehavior(bhName);
behavior.baseObject = null;

const bhName = eventsFunctionContext.getBehaviorName("Behavior");
const obj = objects[0];
const behavior = obj.getBehavior(bhName);
if (runtimeScene.__allPinnedTo){
    runtimeScene.__allPinnedTo.delete(behavior);    
}
